changeSet.sql:
DROP TRIGGER IF EXISTS after_workflow_insert; DROP TRIGGER IF EXISTS after_workflow_update; DROP TRIGGER IF EXISTS after_submission_insert; DROP TRIGGER IF EXISTS after_submission_update;
CREATE TRIGGER after_workflow_insert AFTER INSERT ON WORKFLOW FOR EACH ROW INSERT INTO AUDIT_WORKFLOW_STATUS (workflow_id, status, timestamp) VALUES (NEW.ID, NEW.STATUS, NEW.STATUS_LAST_CHANGED);
CREATE TRIGGER after_workflow_update AFTER UPDATE ON WORKFLOW FOR EACH ROW INSERT INTO AUDIT_WORKFLOW_STATUS (workflow_id, status, timestamp) VALUES (NEW.ID, NEW.STATUS, NEW.STATUS_LAST_CHANGED);
CREATE TRIGGER after_submission_insert AFTER INSERT ON SUBMISSION FOR EACH ROW INSERT INTO AUDIT_SUBMISSION_STATUS (submission_id, status, timestamp) VALUES (NEW.ID, NEW.STATUS, CURRENT_TIMESTAMP);
CREATE TRIGGER after_submission_update AFTER UPDATE ON SUBMISSION FOR EACH ROW INSERT INTO AUDIT_SUBMISSION_STATUS (submission_id, status, timestamp) VALUES (NEW.ID, NEW.STATUS, CURRENT_TIMESTAMP);

changeSet.rollback:
DROP TRIGGER IF EXISTS after_workflow_insert;
DROP TRIGGER IF EXISTS after_workflow_update;
DROP TRIGGER IF EXISTS after_submission_insert;
DROP TRIGGER IF EXISTS after_submission_update;
