changeSet.createProcedure:
CREATE PROCEDURE createAttributeTempTables ()
BEGIN
create temporary table EXPREVAL_TEMP (id bigint(20) unsigned NOT NULL, name VARCHAR(254) NOT NULL, transaction_id CHAR(36) NOT NULL);
create temporary table ENTITY_ATTRIBUTE_TEMP (id bigint(20) unsigned NOT NULL AUTO_INCREMENT primary key, namespace text NOT NULL, name text NOT NULL, value_string text, value_json longtext, value_number double DEFAULT NULL, value_boolean bit(1) DEFAULT NULL, value_entity_ref bigint(20) unsigned DEFAULT NULL, list_index int(11) DEFAULT NULL, list_length int(11) DEFAULT NULL, owner_id bigint(20) unsigned NOT NULL, deleted bit(1) DEFAULT false, deleted_date timestamp NULL DEFAULT NULL, transaction_id CHAR(36) NOT NULL);
create temporary table WORKSPACE_ATTRIBUTE_TEMP (id bigint(20) unsigned NOT NULL AUTO_INCREMENT primary key, namespace text NOT NULL, name text NOT NULL, value_string text, value_json longtext, value_number double DEFAULT NULL, value_boolean bit(1) DEFAULT NULL, value_entity_ref bigint(20) unsigned DEFAULT NULL, list_index int(11) DEFAULT NULL, list_length int(11) DEFAULT NULL, owner_id binary(16) NOT NULL, deleted bit(1) DEFAULT false, deleted_date timestamp NULL DEFAULT NULL, transaction_id CHAR(36) NOT NULL);
END
